        <script>
            function buybtn(){

                var si = $("#customsize").val();
                var amt = 0
                if(si=='S'){
                    amt = 1000;
                    $("#amounthidden").val(amt);
                }else if(si=='M'){
                    amt = 1200;
                    $("#amounthidden").val(amt);
                }else if(si=='L'){
                    amt = 1400;
                    $("#amounthidden").val(amt);
                }else if(si=='XL'){
                    amt = 1600;
                    $("#amounthidden").val(amt);
                }else if(si=='2XL'){
                    amt = 2000;
                    $("#amounthidden").val(amt);
                }

                $.ajax({
                    url: "ajax.php?action=addcustom", 
                    type: "POST",
                    data: {   
                        user_id : <?php echo $_SESSION['regid']; ?>,    
                        tcolor :  $("#topcolorhidden").val(),
                        bcolor :  $("#bottomcolorhidden").val(),
                        neckline :  $("#necklinehidden").val(),
                        sleeve :  $("#sleevehidden").val(),
                        bottom :  $("#bottomhidden").val(),
                        size : $("#customsize").val(),
                        amount : amt
                    },
                    //cache: false,
                    success: function(result){
                        console.log(result);    
                        $("#checkout-selection").submit();             
                    }
                });
            }


            $('select').on('change', function() {
                 if(this.value=='S'){
                    $('#totalamt').html('1000');
                 }else if(this.value=='M'){
                    $('#totalamt').html('1200');
                 }else if(this.value=='L'){
                    $('#totalamt').html('1400');
                 }else if(this.value=='XL'){
                    $('#totalamt').html('1600');
                 }else if(this.value=='2XL'){
                    $('#totalamt').html('2000');
                 }
            });

        </script>


